example.code:
MailMerge mailMerge = new MailMerge();
Dictionary<string, Object> dataSource = new Dictionary<string, Object>()
 {
     {"SenderName", "Gnostice"},
     {"ToName", "Somebody"}
};
mailMerge.MergeInstanceBreakType = MergeInstanceBreakType.Document;
mailMerge.TemplateFileName = inputDocDirectory + "Letter.docx";
mailMerge.DataSource = dataSource;
SaveToFileAction saveAction = new SaveToFileAction("c:\\", "Letter to {{ToName}}", ".docx");	
mailMerge.Merge(saveAction);
